import React, { useEffect, useState } from 'react'
import { Redirect } from 'react-router-dom'
import { Container, InputContainer, TasksContainer } from './styles'
import Input from '../../components/Input'
import Button from '../../components/Button'
import { useForm } from 'react-hook-form'
import {FiEdit2} from 'react-icons/fi'
import Card from '../../components/Card'
import api from '../../services/api'

const Dashboard = ({ authenticated }) => {
  const [tasks, setTasks] = useState([])
  const [token] = useState(localStorage.getItem("@Doit:token")) || ""
  const {register, handleSubmit} = useForm()

  const loadTasks = () => {
    api.get('/task', {
      headers: {
        Authorization: `Bearer ${token}`
      },
      params: {
        completed: false
      }
    })
    .then(response => setTasks(response.data.data))
    .catch(err => console.log(err))
  }

  useEffect(() => {
    loadTasks()
  }, [loadTasks])

  if(!authenticated) {
    return <Redirect to="/login" />
  }


  return (
    <Container>
      <InputContainer>
        <time>7 de maio de 2021</time>
        <section>
          <Input icon={FiEdit2} placeholder="Nova tarefa" register={register} name="task" error=""/>
          <Button type="submit">Adicionar</Button>
        </section>
      </InputContainer>
      <TasksContainer>
        {tasks.map(task => <Card key={task._id} title={task.description} date={task.createdAt} onClick={() => {}} />)}
      </TasksContainer>
    </Container>
  )
}

export default Dashboard
